package java_assignment_2_statements;

public class Assignment6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float x = 0.2112f;
		
		if (x == 3) {
			System.out.print("x is equal to 3");
		} else if (x > 5) {
			System.out.print("x is greater than 5");
		} else if (x <= 0){
			System.out.print("x is less than or equal to 0");
		} else {
			System.out.print("x is none of the other options");
		}

	}

}
